INSERT INTO thai_movies (title, description, director, year, image, created_at, updated_at) VALUES
('2gether The Movie เพราะเราคู่กัน', 'นำแสดงโดย วชิรวิชญ์ ชีวอารี และ เมธวิน โอภาสเอี่ยมขจร ถือเป็นภาพยนตร์ยาวเรื่องแรกจากจีเอ็มเอ็มทีวี ตัวภาพยนตร์เป็นการนำซีรีส์ เพราะเราคู่กัน และ เพราะเรา(ยัง)คู่กัน ที่ออกอากาศทางสถานีโทรทัศน์จีเอ็มเอ็ม 25 มาเรียบเรียงเนื้อหาและตัดต่อใหม่ทั้งหมด พร้อมทั้งถ่ายทำฉากเชื่อมต่อเพิ่มเติม และปรับเนื้อหาในตอนจบใหม่ทั้งหมดเพื่อให้มีความแตกต่างจากฉบับซีรีส์', 'Weerachit Thongjila', 2021, 'https://i.mydramalist.com/2qkedc.jpg', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP),
('Why R U The Movie เพราะรักใช่เปล่า', 'การนำเสนอคู่แรกจะเป็นแนวโรแมนติก คอมเมดี้ (romantic comedy) ภายใต้บริบทของวัฒนธรรมวายไทย ส่วนคู่หลังจะเป็นแนวโรแมนติก ดราม่า (romantic drama) ภายใต้พัฒนาการในการยอมรับและเปิดเผยตัวตน (come out of the closet) ของ LGBTQ', 'Thanamin Wongskulphat', 2020, 'https://gailcarriger.com/wp-content/uploads/2020/07/Why-R-U-500x750.jpg', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP),
('Tharn Type The Series เกลียดนักมาเป็นที่รักซะดีๆ', 'เรื่องราวของคนที่ต่างกันสุดขั้ว แต่จะต้องมาเป็นรูมเมตกัน จากนิยายสุดฟินผลงานของนักเขียนชื่อดัง MAME สู่ซีรีส์บนหน้าจอทีวีเป็นครั้งแรก', 'Lit Samajarn', 2020, 'https://th.bing.com/th/id/OIP.03-eokGGJ_vrrB7JzKGHZgAAAA', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP),
('Theory of Love ทฤษฎีจีบเธอ', 'นำแสดงโดย อรรถพันธ์ พูลสวัสดิ์ (กัน) และ จุมพล อดุลกิตติพร (ออฟ) เป็นเรื่องราวของนักศึกษาวิชาภาพยนต์อย่าง เติร์ด (อรรถพันธ์ พูลสวัสดิ์) ที่แอบชอบเพื่อนสนิทที่ชื่อ ค่าย (จุมพล อดุลกิตติพร) มาเป็นเวลา 3 ปี กับผลจากความรักที่ไม่สมหวังเติร์ดตัดสินใจที่จะหยุดความรู้สึกของเขา ไม่ว่าจะยากแค่ไหนแต่เมื่อเติร์ดตัดสินใจที่จะหยุดความรู้สึกนี้ กลับกันค่ายก็รู้ตัวโดยบังเอิญว่าเขาก็มีความรู้สึกต่อเติร์ดมากกว่าคำว่าเพื่อนไปแล้ว', 'Nuttapong Mongkolsawas', 2021, 'https://uncommontrax.com/wp-content/uploads/2020/07/19202BD5-3270-40A9-BE74-C58289B24381.jpeg', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP),
('Love Sick 2024 ชุลมุนรักกางเกงน้ำเงิน', 'ความรักของตัวละครหลัก โน่ และปุณณ์ ที่ไม่เพียงแต่สร้างความสนุกสนาน แต่ยังถ่ายทอดบทเรียนชีวิตที่สำคัญในวัยรุ่น นอกจากนี้ เราจะได้เห็นความหล่อ ความน่ารักและความสามารถที่เหนือชั้นของโน่กับปุณณ์อีกด้วย', 'Naruebet Kuno', 2020, 'https://i.mydramalist.com/RBblnY_4c.jpg', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP),
('KinnPorsche The Series คินน์พอร์ช เดอะ ซีรีส์', 'เรื่องราวของคินน์ ทายาทตระกูลมาเฟีย ที่ได้พบกับพอร์ช ผู้ซึ่งช่วยชีวิตเขาไว้และตกกระไดพลอยโจนกลายเป็นบอดี้การ์ดของเขา ทั้งคู่ได้เผชิญกับปริศนาและความลับต่าง ๆ ที่ค่อย ๆ เผยอดีตของพวกเขา', 'Kongkiat Komesiri', 2022, 'https://media.themoviedb.org/t/p/w300_and_h450_bestv2/ndLC9UNpDPqHdNtKvouKyQ0gXuk.jpg', CURRENT_TIMESTAMP, CURRENT_TIMESTAMP);
